import axios from "axios";
import Context from "./Context";
import jwt_decode from "jwt-decode";
import { render } from "@testing-library/react";
import {
  Navbar,
  Nav,
  NavDropdown,
  Form,
  FormControl,
  Button,
} from "react-bootstrap";
import React, { Component } from "react";
import { Switch, Route, Link, BrowserRouter as Router } from "react-router-dom";

import Cart from "./components/Cart";
import Login from "./components/Login";
import ProductList from "./components/ProductList";
import AddProducts from "./components/AddProducts";

export default class App extends Component {
constructor(props){
  super(props)
      this.state = {
      user: null,
      cart: {},
      products: [],
      showMenu: null,
    };
    this.routerRef = React.createRef();

}


  async componentDidMount() {

    let user = localStorage.getItem("user");
    const products = await axios.get('http://localhost:3001/products');
    user = user ? JSON.parse(user) : null;
    this.setState({ user,  products: products.data });
    console.log(this.state.user)


  }


  login = async (email, password) => {
    const res = await axios
      .post("http://localhost:3001/login", { email, password })
      .catch((res) => {
        return { status: 404, message: "unauthorised user" };
      });

    if (res.status === 200) {
      console.log("login success");
      const { email } = jwt_decode(res.data.accessToken);

      const user = {
        email,
        token: res.data.accessToken,
        accessToken: email === "admin@example.com" ? 0 : 1,
      };

      this.setState({user});

      localStorage.setItem("user", JSON.stringify(user));
      return true;
    } else {
      return false;
    }
  };

  addToCart = (Products) => {
    const cart = Products;
    //  this.setState({cart})
    //  console.log(this.state.cart)
  };

  AddProducts = (product, callback) => {
    const products = this.state.products.slice();
    products.push(product);
    this.setState({ products }, () => {
      callback && callback();
    });
  };

  logout = () => {
    console.log("logout called");
    // e.preventDefault();

    this.setState({ user: null });
    localStorage.removeItem("user");
  };

  render() {
    return (
      <Context.Provider
        value={{
          ...this.state,
          login: this.login,
          addToCart: this.addToCart,
        }}
      >
        <div>
          <Navbar bg="light" expand="lg">
            <Navbar.Brand href="">Ecommerse</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="mr-auto">
                <Nav.Link href={this.state.user ? "/cart" : "/login"}>
                  Cart
                </Nav.Link>
                <Nav.Link href="/products">Products</Nav.Link>
                <Nav.Link href="/Add-products">Add Products</Nav.Link>
                <NavDropdown
                  title={!this.state.user ? "account" : this.state.user}
                  id="basic-nav-dropdown"
                >
                  {/* {!this.state.user.length ? ( */}
                  <NavDropdown.Item href="/login">Login</NavDropdown.Item>
                  {/* ) : ( */}
                  <NavDropdown.Item onClick={this.logout}>
                    Logout
                  </NavDropdown.Item>
                  {/* )} */}
                </NavDropdown>
              </Nav>
              <Form inline>
                <FormControl
                  type="text"
                  placeholder="Search"
                  className="mr-sm-2"
                />
                <Button variant="outline-success">Search</Button>
              </Form>
            </Navbar.Collapse>
          </Navbar>

          <Router>
            <Switch>
              <Route path="/cart" component={Cart} />
              <Route exact path="/products" component={ProductList} />
              <Route exact path="/login" component={Login} />
              <Route exact path="/Add-products" component={AddProducts} />
              <Route exact path="/" component={ProductList} />
            </Switch>
          </Router>
        </div>
      </Context.Provider>
    );
  }
}
