import React, { Component } from "react";
import { Form, Button } from "react-bootstrap";
import withContext from "../withContext";
import { Redirect } from "react-router-dom";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: "",
    };
  }

  // componentDidMount(){
  //   if(this.props.context.user){
  //     console.log("user is here in the login")
  //   }else{
  //     console.log("user is not in the login")
  //   }
  // }

  handleChange = (e) =>
    this.setState({ [e.target.name]: e.target.value, error: "" });

  login = (e) => {
    e.preventDefault();
    const { username, password } = this.state;
    console.log(username, password);
    if (!username || !password) {
      console.log("fill the form");
      return this.setState({ error: "fill the required fields" });
    }
    this.props.context.login(username, password).then((loggedIn) => {
      if (!loggedIn) {
        this.setState({ error: "Invalid credentials" });
      }
    });
  };

  render() {
    return !this.props.context.user.length ? (
      <>
      <Form onSubmit={this.login}>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control
            onChange={this.handleChange}
            name="username"
            type="email"
            placeholder="Enter email"
          />
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text>
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control
            onChange={this.handleChange}
            name="password"
            type="password"
            placeholder="Password"
          />
        </Form.Group>
        <div>
          {" "}
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </div>
      </Form>
      </>
    ) : (

      <Redirect to="/products" />
    );
  }
}
export default withContext(Login);
