import { render } from "@testing-library/react";
import React, { Component } from "react";
import { Switch, Route, Link, BrowserRouter as Router } from "react-router-dom";

import Cart from "./components/Cart";
import Login from "./components/Login";
import ProductList from "./components/ProductList";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: null,
      cart: {},
      products: [],
      showMenu: null,
    };
  }

  render() {
    return (
      <Router>
        <div className="app">
          <nav className="navbar conatiner">
            <div className="navbar brand">
              <b className="navbar-heading">ecommerse</b>
              <label
                className="navbar"
                aria-label="menu"
                onClick={(e) => {
                  e.preventDefault();
                  this.setState({ showMenu: !this.state.showMenu });
                }}
              >

                <Link to="/Cart" >cart
                {Object.keys(this,state.cart).length}
                </Link>
                <Link to="/Products" >Products</Link>
                <Link to="/addProducts" ></Link>
                <Link to="/Cart" >cart</Link>
                {!this.state.user ? (
                  <Link to="/Login" >Login</Link>
                ):
                <Link to="/logout" >logout</Link>
                }


              </label>
            </div>
          </nav>
          <Switch>
            <Route exact path="/Products" components={ProductList}  />
            <Route exact path="/addProducts" components={ProductList}  />
            <Route exact path="/Cart" components={Cart}  />
          </Switch>
        </div>
      </Router>
    );
  }
}
