import axios from "axios";
import jwt_decode from "jwt-decode";
import { render } from "@testing-library/react";
import {
  Navbar,
  Nav,
  NavDropdown,
  Form,
  FormControl,
  Button,
} from "react-bootstrap";
import React, { Component } from "react";
import { Switch, Route, Link, BrowserRouter as Router } from "react-router-dom";

import Cart from "./components/Cart";
import Login from "./components/Login";
import ProductList from "./components/ProductList";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      user: null,
      cart: {},
      products: [],
      showMenu: null,
    };
  }
  componentDidMount() {
    let user = localStorage.getItem("user");
    user = user ? JSON.parse(user) : null;
    this.setState({ user });
  }

  login = async (email, password) => {
    const res = await axios
      .post("https://localhost:3001/login", { email, password })
      .catch((res) => {
        return { status: 404, message: "unauthorised user" };
      });
  };

  render() {
    return (
      <div>
        <Navbar bg="light" expand="lg">
          <Navbar.Brand href="">Ecommerse</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
              <Nav.Link href={this.state.user ? "/cart" : "/login"}>
                Cart
              </Nav.Link>
              <Nav.Link href="/products">Products</Nav.Link>
              <NavDropdown
                title={!this.state.user ? "account" : this.state.user}
                id="basic-nav-dropdown"
              >
                {!this.state.user ? (
                  <NavDropdown.Item href="/login">Login</NavDropdown.Item>
                ) : (
                  <NavDropdown.Item href="/logout">Logout</NavDropdown.Item>
                )}
              </NavDropdown>
            </Nav>
            <Form inline>
              <FormControl
                type="text"
                placeholder="Search"
                className="mr-sm-2"
              />
              <Button variant="outline-success">Search</Button>
            </Form>
          </Navbar.Collapse>
        </Navbar>

        <Router>
          <Switch>
            <Route path="/cart" component={Cart} />
            <Route exact path="/products" />
            <Route exact path="/login" component={Login} />
          </Switch>
        </Router>
      </div>
    );
  }
}
