import React from "react";
import { Card, Button } from "react-bootstrap";

const CartItem = ({cartItem, removeFromCart}) => {
  console.log(cartItem);
  return (
    <div className="m-3" style={{ height: "25rem" }}>
      <Card style={{ height: "30rem", width: "18rem" }}>
        <Card.Img
          style={{ height: "16rem" }}
          src={cartItem.product.image}
        />
        <div className="mb-1">
          <Card.Body style={{}}>
            <Card.Title> {cartItem.id} </Card.Title>
            <p>Price:{cartItem.product.price}</p>
            <Card.Text>{cartItem.amount}</Card.Text>
            <Button variant="primary" onClick={() => removeFromCart({id:cartItem.id, cartItem , amount:1})}>
              remove from cart
            </Button>
          </Card.Body>
        </div>
      </Card>
    </div>
  );
};

export default CartItem;
